userDefinedLinkHintCss:
div > .vimiumHintMarker {
  /* linkhint boxes */
  background: #181616;
  border: 1px solid #C8C093;
  box-shadow: none;
  padding: 2px 4px;
}

div > .vimiumHintMarker span {
  /* linkhint text */
  color: #CCCFCC;
  font-size: 10px;
  font-weight: 600;
  text-shadow: none;
  box-shadow: none;
}

div > .vimiumHintMarker > .matchingCharacter {
  color: #C8C093;
}
